import Display from './components/Display';
import './App.css';
import React ,{useState} from "react";
import Calc from './components/Calc';
function App() {
  let equ="";
  const setEquation=(e)=>{
    equ=equ+e;
    console.log(equ)
  }
  return (
    <div className="main">
     <h1>Calculator</h1>
     <div className='displayContainer'>
     <Display equ={equ}/>
     </div>
     <div className='calcContainer'>

      <Calc getEquation={setEquation}/>
     </div>
    </div>
  );
}

export default App;
