import React, {  useState } from "react";
import Buttons from "./Buttons";
import style from './Calc.module.css';
const Calc=(props)=>{
    const [equation,setButton]=useState("");
    const [temp,setTemp]=useState(0);
    const [operator,setOperator]=useState(null);
   // console.log("in cal "+equation);

    let count=0;
    const setEquation=(e)=>{

        let t1=0;
        let t2=0;
        let t3=0;
        if(e==="="){
            console.log("ini "+temp);
            t1=temp;
            t2=parseInt(equation);
            if(operator==="+"){
            t3=t1+t2;
            }
            else if(operator==="-"){
                t3=t1-t2;
                }
            console.log("after "+t3);
            setButton((equation)=>t3)
            setTemp((temp)=>equation)

        }
        else if(e==="DEL"){
            setButton((equation)=>equation.slice(0,-1))
        }
        else if(e==="+" ||e==="-" ||e==="*" ||e==="/"  ){
            if(count!==0){
            t1=temp;
            t2=parseInt(equation);
            if(e==="+")
            {
            t3=t1+t2;
            setTemp((temp)=>t3)
            console.log("in add"+parseInt(equation))
            setOperator(e);
            }
            if(e==="-")
            {
            t3=t1-t2;
            setTemp((temp)=>t3)
            console.log("in add"+parseInt(equation))
            setOperator(e);
            }
            if(e==="*")
            {
            t3=t1+t2;
            setTemp((temp)=>t3)
            console.log("in add"+parseInt(equation))
            setOperator(e);
            }
            setButton("");
        }

        }
        else if(e==="AC"){
            setButton("")
            setOperator(null)
            setTemp(0)
        }
        else{
        setButton((equation)=>equation+e);
        }   



    }
    console.log(equation);
    props.getEquation(equation)
    return(
        <div>
            <div className={style.line_one}>
            <Buttons value="AC" equ={setEquation} />
            <Buttons value="DEL"  equ={setEquation} />
            <Buttons value="-/+" equ={setEquation} />
            <Buttons value="/"  equ={setEquation} />
            </div>
            <div className={style.line_one}>
            <Buttons value="7"equ={setEquation} />
            <Buttons value="8" equ={setEquation}/>
            <Buttons value="9" equ={setEquation}/>
            <Buttons value="*" equ={setEquation}/>
            </div>
            <div className={style.line_one}>
            <Buttons value="4" equ={setEquation}/>
            <Buttons value="5" equ={setEquation}/>
            <Buttons value="6" equ={setEquation} />
            <Buttons value="-" equ={setEquation} />
            </div>
            <div className={style.line_one}>
            <Buttons value="1"  equ={setEquation}/>
            <Buttons value="2" equ={setEquation}/>
            <Buttons value="3" equ={setEquation}/>
            <Buttons value="+" equ={setEquation}/>
            </div>
            <div className={style.line_one}>
            <Buttons value="%" equ={setEquation}/>
            <Buttons value="0" equ={setEquation}/>
            <Buttons value="." equ={setEquation}/>
            <Buttons value="=" equ={setEquation}/>
            </div>
        </div>
    );
};
export default Calc;
