import React, { useEffect, useState } from "react";
import Buttons from "./Buttons";
import style from './Calc.module.css';
const Calc=(props)=>{
    const [equation,setButton]=useState("");

    useEffect=((e)=>{
        setButton((equation)=>equation+e)
    },[equation]);

    return(
        <div>
            <div className={style.line_one}>
            <Buttons value="AC" onValue={setButton}/>
            <Buttons value="DEL" onValue={setButton} />
            <Buttons value="//"onValue={setButton} />
            <Buttons value="/" onValue={setButton}/>
            </div>
            <div className={style.line_one}>
            <Buttons value="7"onValue={setButton} />
            <Buttons value="8" onValue={setButton}/>
            <Buttons value="9" onValue={setButton}/>
            <Buttons value="*" onValue={setButton}/>
            </div>
            <div className={style.line_one}>
            <Buttons value="4" onValue={setButton}/>
            <Buttons value="5" onValue={setButton}/>
            <Buttons value="6" onValue={setButton} />
            <Buttons value="-" onValue={setButton} />
            </div>
            <div className={style.line_one}>
            <Buttons value="1"  />
            <Buttons value="2" />
            <Buttons value="3" />
            <Buttons value="+" />
            </div>
            <div className={style.line_one}>
            <Buttons value="%" />
            <Buttons value="0" />
            <Buttons value="." />
            <Buttons value="=" />
            </div>
        </div>
    );
};
export default Calc;
