import React, { useState } from "react";
import Buttons from "./Buttons";
import style from './Calc.module.css';
const Calc=(props)=>{
    const [buttonEqu,setButtonEqu]=useState("");
   // console.log(calcEqu )
    const setEquation=(e)=>{
        setButtonEqu((buttonEqu)=>buttonEqu+e)
        console.log(buttonEqu)
    }
    return(
        <div>
            <div className={style.line_one}>
            <Buttons value="AC" onValue={setEquation}/>
            <Buttons value="DEL" onValue={setEquation} />
            <Buttons value="//" onValue={setEquation}/>
            <Buttons value="/" onValue={setEquation}/>
            </div>
            <div className={style.line_one}>
            <Buttons value="7" onValue={setEquation}/>
            <Buttons value="8" onValue={setEquation}/>
            <Buttons value="9"onValue={setEquation}/>
            <Buttons value="*"onValue={setEquation} />
            </div>
            <div className={style.line_one}>
            <Buttons value="4" onValue={setEquation}/>
            <Buttons value="5" onValue={setEquation}/>
            <Buttons value="6" onValue={setEquation}/>
            <Buttons value="-" onValue={setEquation}/>
            </div>
            <div className={style.line_one}>
            <Buttons value="1" onValue={setEquation} />
            <Buttons value="2" onValue={setEquation}/>
            <Buttons value="3" />
            <Buttons value="+" />
            </div>
            <div className={style.line_one}>
            <Buttons value="%" />
            <Buttons value="0" />
            <Buttons value="." />
            <Buttons value="=" />
            </div>
        </div>
    );
};
export default Calc;
